import React, { useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { ErrorMessage } from '@hookform/error-message';

import { FileLoader } from 'components/file-upload';
import { Button } from 'components/app';
import {
  LocalAtmIcon,
  LocationCityIcon,
  StreetviewIcon,
  BurstModeIcon,
} from 'components/icons';

const Error = ({ children }) => (
  <div className='input__notValid'>{children}</div>
);

const AddRentalForm = ({ onSubmit }) => {
  const { register, handleSubmit, errors, setValue } = useForm();

  useEffect(() => {
    register({ name: 'image' });
  }, [register]);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className='inputs'>
        <div className='input'>
          <input
            ref={register({
              required: 'Title is Required',
              maxLength: {
                value: 128,
                message: 'Title must be 128 characters maximum',
              },
            })}
            type='text'
            name='title'
            id='title'
            placeholder='Name'
          />
          <ErrorMessage as={<Error />} errors={errors} name='title'>
            {({ message }) => <p>{message}</p>}
          </ErrorMessage>
        </div>
        <div className='input'>
          <select
            ref={register({
              required: 'Category is Required',
            })}
            name='category'
            id='category'
            placeholder='Category'
          >
            <option selected disabled defaultValue>
              Choose Category
            </option>
            <option value='apartment'>Apartment</option>
            <option value='condo'>Condo</option>
            <option value='house'>House</option>
            <option value='unique Stay'>Unique Stay</option>
            
          </select>
          <ErrorMessage as={<Error />} errors={errors} name='category'>
            {({ message }) => <p>{message}</p>}
          </ErrorMessage>
        </div>
        <div className='input'>
          <div className='icon-input'>
            <input
              ref={register({
                required: 'City is Required',
              })}
              type='text'
              name='city'
              id='city'
              placeholder='City'
            />
            <LocationCityIcon />
          </div>
          <ErrorMessage as={<Error />} errors={errors} name='city'>
            {({ message }) => <p>{message}</p>}
          </ErrorMessage>
        </div>
        <div className='input'>
          <div className='icon-input'>
            <input
              ref={register({
                required: 'Street is Required',
                minLength: {
                  value: 4,
                  message: 'Street must 4 characters minimum',
                },
              })}
              type='text'
              name='street'
              id='street'
              placeholder='Street'
            />
            <StreetviewIcon />
          </div>
          <ErrorMessage as={<Error />} errors={errors} name='street'>
            {({ message }) => <p>{message}</p>}
          </ErrorMessage>
        </div>
        <div className='input'>
          <input
            ref={register({
              required: 'Number of Rooms is Required',
            })}
            type='number'
            name='numOfRooms'
            id='numOfRooms'
            placeholder='Number of Rooms'
          />
          <ErrorMessage as={<Error />} errors={errors} name='numOfRooms'>
            {({ message }) => <p>{message}</p>}
          </ErrorMessage>
        </div>
        <div className='input'>
          <textarea
            ref={register({
              required: 'Description is Required',
            })}
            rows='8'
