              type='text'
              name='image3'
              id='image3'
              placeholder='Image-3 URL'
            />
            <BurstModeIcon />
          </div>
          <ErrorMessage as={<Error />} errors={errors} name='image3'>
            {({ message }) => <p>{message}</p>}
          </ErrorMessage>
        </div>
        <div className='input '>
          <div className='icon-input'>
            <input
              ref={register({
                required: 'Image is Required',
              })}
              type='text'
              name='image4'
              id='image4'
              placeholder='Image-4 URL'
            />
            <BurstModeIcon />
          </div>
          <ErrorMessage as={<Error />} errors={errors} name='image4'>
            {({ message }) => <p>{message}</p>}
