        </div>
        <div className='input '>
          <div className='icon-input'>
            <input
              ref={register({
                required: 'Daily Price is Required',
              })}
              type='number'
              min='0'
              name='dailyPrice'
              id='dailyPrice'
              placeholder='Daily Price'
            />
            <LocalAtmIcon />
          </div>
          <ErrorMessage as={<Error />} errors={errors} name='dailyPrice'>
            {({ message }) => <p>{message}</p>}
          </ErrorMessage>
        </div>
      </div>
      <Button type='submit' text='Add' />
    </form>
  );
};

